mapper.sql:
from
betterBlog as a
left join user as b on b.seq =
a.user_seq
left join
blogCategory as c on c.seq = a.blogCategory_seq
where 1=1 
and a.blogCategory_seq = #{blogCategory_seq}


AND a.bgDelNy = #{shDelNy}


	AND a.bgReg BETWEEN #{shDateStartDB} AND
		#{shDateEndDB}

	AND a.bgMof BETWEEN #{shDateStartDB} AND
		#{shDateEndDB}




	AND a.seq = #{shValue}
	AND a.title LIKE CONCAT('%',#{shValue},'%')
	AND b.nickName LIKE CONCAT('%',#{shValues},'%')

from betterBlog as a left join user as b on b.seq = a.user_seq left join blogCategory as c on c.seq = a.blogCategory_seq where 1=1
AND a.bgDelNy = #{shDelNy}
AND a.bgReg BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND a.bgMof BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND a.seq = #{shValue} AND a.title LIKE CONCAT('%',#{shValue},'%') AND b.name LIKE CONCAT('%',#{shValue},'%')

mapper.delete:
delete from image
where 1=1
and pSeq = #{seq}

delete from wishlist
where 1=1
and user_seq =
#{user_seq}
betterBlog_seq = #{betterBlog_seq}

update clotheInfo
set
ifDelNy = 1
where 1=1
and seq = #{infoseq}
and betterBlog_seq = #{betterBlog_seq}
