mapper.sql:
from
user as a
left join code as b on a.gender = b.seq
where 1=1

AND a.urDelNy = #{shDelNy}


AND a.dob BETWEEN 
	#{shDateStart} AND #{shDateEnd} 
	AND 
	a.urReg BETWEEN #{shDateStartDB} AND #{shDateEndDB} 
	AND 
	a.urMof BETWEEN #{shDateStartDB} AND #{shDateEndDB} 


	AND a.seq = #{shValue}
	AND a.name LIKE CONCAT('%',#{shValue},'%')
	AND a.nickName LIKE CONCAT('%',#{shValue},'%')
	AND b.cdName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
update user
set
password = #{password}
where 1=1
and seq = #{urSeq}
